function OpenModal(divlayer) {

            //聲明層不可以點擊灰色區域關閉
            $("#" + divlayer).modal({ backdrop: 'static', keyboard: false });
            //顯示層
            $("#" + divlayer).modal('show');
};

//关闭层
function CloseModal(divlayer) {
            $("#" + divlayer).modal('hide');
};

