        //控制区域展示
function ControlAreaShowLang(divHead, divName,lang) {
    var curTxt = $("#" + divHead).text();
//    var aa = '+expanse';
    //    var bb = '-collapsed';
    var aa =ControlArea_Expanse(lang);//+展開
    var bb =ControlArea_Collapsed(lang); //'-收起';
    if (curTxt == bb) {
        $("#" + divHead).text(aa);
        $("#" + divName).hide();
    } else {
        $("#" + divHead).text(bb);
        $("#" + divName).show();
    }
};

//+展开
function ControlArea_Expanse(lang) {
    switch (lang) {
        case 'zh-TW':
             return   '+展開';    
        case 'en':
            return  '+Expand';
        case 'en-US':
            return   '+Expand';
        case 'zh-CN':
            return      '+展开';
         case 'vi':
            return      '+mở ra';
        default:
            return     '+展开';
    }
}

//-收起
function ControlArea_Collapsed(lang) {
    switch (lang) {
        case 'zh-TW':
             return   '-收起';    
        case 'en':
            return  '-Collapse';
        case 'en-US':
            return    '-Collapse';
        case 'zh-CN':
            return       '-收起'; 
         case 'vi':
            return      '-thu lại';   
        default:
            return     '-收起';    
    }
}
